mapper.select:
SELECT EMP_ID, DEPT_NAME, JOB_NAME, USER_NAME, ATT_NO ,ATT_DATE, ATT_INTIME, ATT_OUTTIME, RIGHT_NAME FROM EMPLOYEE JOIN JOB USING (JOB_CODE) JOIN DEPARTMENT USING (DEPT_CODE) JOIN MEMBER USING (EMP_ID) JOIN ATTENDANCE USING (EMP_ID) JOIN RIGHT USING (RIGHT_NO) WHERE ATT_DATE = #{attDate} AND ATT_STATUS_NO =#{attStatusNo}
ORDER BY DEPT_CODE, ATT_DATE
ORDER BY JOB_CODE, ATT_DATE

SELECT EMP_ID, DEPT_NAME, JOB_NAME, USER_NAME, ATT_NO ,ATT_DATE, ATT_INTIME, ATT_OUTTIME, RIGHT_NAME FROM EMPLOYEE JOIN JOB USING (JOB_CODE) JOIN DEPARTMENT USING (DEPT_CODE) JOIN MEMBER USING (EMP_ID) JOIN ATTENDANCE USING (EMP_ID) JOIN RIGHT USING (RIGHT_NO) WHERE ATT_DATE like '%' || #{attMonthDate} || '%' AND ATT_STATUS_NO =#{attStatusNo}
ORDER BY DEPT_CODE, ATT_DATE
ORDER BY JOB_CODE, ATT_DATE

mapper.update:
update attendance set
ATT_INTIME = #{attInTime},
ATT_OUTTIME = #{attOutTime}
WHERE EMP_ID= #{empId} and  ATT_NO = #{attNo}

update attendance set
ATT_INTIME = #{attInTime},
ATT_OUTTIME = #{attOutTime}
WHERE EMP_ID= #{empId} and  ATT_NO = #{attNo}

update attendance set
ATT_STATUS_NO = #{attStatusNo}		
WHERE EMP_ID= #{empId} and  ATT_NO = #{attNo}
